<?phpclass menu {		/**	 * Enter description here...	 *	 */	function __construct() {		//TO DO...	}		/**	 * 左边菜单列表	 *	 * @param int $pID	 * @return array	 */	function getMenuList($pID) 	{		global $db,$tablepre;		 		$sSQL = "SELECT * FROM `{$tablepre}menu` WHERE pID = $pID or id =$pID ORDER BY orderID";		$aMenuList = $db->getRecordSet($sSQL);		return $aMenuList;	}		/**	 * Enter description here...	 *	 * @param unknown_type $iPowerID	 */	function getPowerNameByID($iPowerID)	{		global $db,$tablepre;			$sSQL = "SELECT id,fileNameCn,powerNameCn FROM `{$tablepre}power` WHERE id=".$iPowerID;		$aInfo = $db->getRecordSet($sSQL, 1);		return $aInfo;	}		/**	 * Enter description here...	 *	 * @param unknown_type $iPowerID	 * @return unknown	 */	function getPowerInfoByID($iPowerID)	{		global $db,$tablepre;			$sSQL = "SELECT * FROM `{$tablepre}power` WHERE id=".$iPowerID;		$aInfo = $db->getRecordSet($sSQL, 1);		return $aInfo;	}}?>